import React, { Component } from 'react';
import axios from 'axios'

export class HTTPPost extends Component {
    constructor(props) {
        super(props)

        this.state = {
             apiresponse: null
        }
    }

    postToApi = () => {
        axios.post(
            'https://jsonplaceholder.typicode.com/posts',
            {
                title: 'Hello world!',
                body: 'It works!',
                userId: 123,
            }
        ).then(response => {
            this.setState({
                apiResponse: response.data
            })
        })
    }

    render() {
        return (
            <div>
