import React, { Component } from 'react'

export class LifeCyclesCWUTracker extends Component {
  constructor(props) {
    super(props)

    this.state = {
      mouseX: 0,
      mouseY: 0,
    }
  }
  trackMouse = e => {
    console.log('Mouse moved')
    this.setState({
      mouseX: e.clientX,
      mouseY: e.clientY,
    })
  }
  componentDidMount() {
    window.addEventListener('mousemove', this.trackMouse);
  }
  componentWillUnmount() {
      console.log('unmounting')
